mapper.update:
update node
set idle_mem_size=idle_mem_size-#{idleMemSize,jdbcType=BIGINT}
where node_id=#{nodeId,jdbcType=VARCHAR} and idle_mem_size-#{idleMemSize,jdbcType=BIGINT} > 0

mapper.select:
select count(1)
from node
where total_mem_size - idle_mem_size / total_mem_size > #{maxMemUsageRatio,jdbcType=DECIMAL}
      and cpu_usage_ratio < #{maxCpuUsageRatio,jdbcType=DECIMAL}
